import React, { useState } from 'react';

const MyChat = () => {

    const [message, setMessage] = useState('');

    const handleMessage = (event) => {
        event.preventDefault();
        setMessage(event.target.value);
    };

    const sendMess = () => {
        console.log(message);
        document.getElementById('messageInput').setAttribute('value', ''); // Очистка текста в инпуте
    };

    return (
        <div className='MyChat'>
            <div className="block-name">
                <h1>Name</h1>
            </div>

            <div className="block-friend-mess">
                Приветsadfsdfsdf
                sdfsdfsdf
                sdfsdfsdf sdfsdfsdfsd
                sdfsdfsdfsd 
                 sdfsdfsdf

                 sdfsdfsdt
                 dsfsdf
                 sde4wahdegdfashrerhyaerhaerhaert
                 sde4wahdegdfashrerhyaerhaerhaerta
            </div>

            <div className="block-my-mess">Привет!</div>

            <div className="block-write">
                <input id="messageInput" onChange={handleMessage} type="text" />
                <button onClick={sendMess}>Отправить</button>
            </div>
        </div>
    );
};

export default MyChat;
