import React, { useState } from 'react';
import '../../style/MainPage.css';
import Search from '../page-component/search'; 

const MenuChats = () => {

    const [isVisibleChats, setVisibleChats] = useState(true); // Создаем состояние для видимости чатов

    const chatBase = [
        ["Егор", true],
        ["Иван", false],
        ["Алексей", true],
        ["Максим", false],
        ["Никита", true],
        ["Сергей", true],
        ["Олег", false],
        ["Андрей", true],
        ["Дмитрий", false],
        ["Владимир", true]
    ];

    const handleSearchClick = () => {
        setVisibleChats(false); // При клике на кнопку поиска скрываем чаты
    }

    return (
        <div className='container-my-chats'>
            <Search onSearchClick={handleSearchClick} />

            {isVisibleChats && (
                <div className='user-list'>
                    {chatBase.map((chat, index) => (
                        <div key={index} className={`block-chat ${chat[1] ? '' : 'hidden'}`}>
                            {/* Добавляем класс 'hidden' к скрытию чата, если второй элемент массива равен false */}
                            <div className="info-chat">
                                <p className='name-user'>{chat[0]}</p>
                                <span className='activ'>{chat[1] ? "online" : "offline"}</span>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default MenuChats;
