import React, { useState } from 'react';
import Search from '../page-component/search';
import '../../style/MainPage.css';

const MenuChats = () => {
    const [isVisibleChats, setVisibleChats] = useState(true);
    const [isSearchVisible, setSearchVisible] = useState(false);
    const [searchedUsers, setSearchedUsers] = useState([]);

    const chatBase = [
        ["Егор", true],
        ["Иван", false],
        ["Алексей", true],
        ["Максим", false],
        ["Никита", true],
        ["Сергей", true],
        ["Олег", false],
        ["Андрей", true],
        ["Дмитрий", false],
        ["Владимир", true]
    ];

    const handleSearchClick = () => {
        setVisibleChats(false);
        setSearchVisible(true);
    };

    const handleBackClick = () => {
        setVisibleChats(true);
        setSearchVisible(false);
    };

    return (
        <div className='container-my-chats'>
            <Search onSearchClick={handleSearchClick} onBackClick={handleBackClick} />

            {isSearchVisible && searchedUsers.length > 0 ? (
                <div className='user-list'>
                    {searchedUsers.map((user, index) => (
                        <div key={index} className={`block-chat`}>
                            <div className="info-chat">
                                <p className='name-user'>{user.username}</p>
                                <span className='activ'>{user.online ? "online" : "offline"}</span>
                            </div>
                        </div>
                    ))}
                </div>
            ) : isSearchVisible && searchedUsers.length === 0 ? (
                <p>Пользователь не найден</p>
            ) : (
                <div className='user-list'>
                    {chatBase.map((chat, index) => (
                        <div key={index} className={`block-chat ${chat[1] ? '' : 'hidden'}`}>
                            <div className="info-chat">
                                <p className='name-user'>{chat[0]}</p>
                                <span className='activ'>{chat[1] ? "online" : "offline"}</span>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default MenuChats;
