import React, { useState } from 'react';
import axios from 'axios';

import '../../style/MainPage.css';

const Search = ({ onSearchClick, onBackClick, setSearchResults }) => {
    const [user, setUser] = useState('');
    const [searchResults, setSearchResultsState] = useState([]); // new state variable to store search results

    const handleUser = (event) => {
        event.preventDefault();
        setUser(event.target.value);
    };

    const handleSearch = async (event) => {
        try {
            const response = await axios.get('http://localhost:7000/search-user', { params: { user } });
            const searchResults = response.data; // assume the API returns an array of user objects
            setSearchResultsState(searchResults); // update the state with the search results
            setSearchResults(searchResults); // update the parent component's state
        } catch (e) {
            console.error('Ошибка при поиске:', e);
        }
    }

    return (
        <div>
            {/* ... */}
            <div className="main-search-container">
                <input onChange={handleUser} onClick={onSearchClick} className='inp-search' type="text" />
                <button className="but-search" onClick={handleSearch}>Найти</button>
                {searchResults.length > 0 && (
                    <div className='search-results'>
                        {searchResults.map((user, index) => (
                            <div key={index} className='block-chat'>
                                <div className="info-chat">
                                    <p className='name-user'>{user.nickname}</p>
                                    {/* display other user information if needed */}
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Search;
