import React, { useState } from 'react';

const MyChat = () => {

    const [message, setMessage] = useState('');
    const [dataMessage, setDataMessage] = useState([]);

    const handleMessage = (event) => {
        event.preventDefault();
        setMessage(event.target.value);
    };

    const sendMess = () => {
        const data = {
            messData: message
        };

        setDataMessage([...dataMessage, data]);
        console.log(dataMessage);
        setMessage(''); // Очистка текста в поле ввода после отправки сообщения
    };

    return (
        <div className='MyChat'>
            <div className="block-name">
                <h1>Name</h1>
            </div>

            <div className="block-friend-mess">
                Приветsadfsdfsdf
                sdfsdfsdf
                sdfsdfsdf sdfsdfsdfsd
                sdfsdfsdfsd 
                 sdfsdfsdf

                 sdfsdfsdt
                 dsfsdf
                 sde4wahdegdfashrerhyaerhaerhaert
                 sde4wahdegdfashrerhyaerhaerhaerta
            </div>

            {dataMessage.map((mess, index) => ( // Добавление скобок {} для корректного отображения JSX в map
                <div key={-index} className="block-my-mess"> {/* Исправление класса на block-friend-mess */}
                    {mess.messData}
                </div>
            ))}

            <div className="block-my-mess">Привет!</div>

            <div className="block-write">
                <input id="messageInput" onChange={handleMessage} type="text" />
                <button onClick={sendMess}>Отправить</button>
            </div>
        </div>
    );
};

export default MyChat;
