import React, { useState } from 'react';
import Search from '../page-component/search';
import '../../style/MainPage.css';

const MenuChats = () => {
    const [chatBase, setChatBase] = useState([
        ["Егор", true],
        ["Иван", false],
        ["Алексей", true],
        ["Максим", false],
        ["Никита", true],
        ["Сергей", true],
        ["Олег", false],
        ["Андрей", true],
        ["Дмитрий", false],
        ["Владимир", true]
    ]);
    const [searchedUsers, setSearchedUsers] = useState([]);

    const handleSearchClick = (results) => {
        setChatBase([]); // Очищаем чаты при поиске
        setSearchedUsers(results);
    };

    const handleBackClick = () => {
        setChatBase([
            ["Егор", true],
            ["Иван", false],
            ["Алексей", true],
            ["Максим", false],
            ["Никита", true],
            ["Сергей", true],
            ["Олег", false],
            ["Андрей", true],
            ["Дмитрий", false],
            ["Владимир", true]
        ]);
        setSearchedUsers([]);
    };

    return (
        <div className='container-my-chats'>
            <Search onSearchClick={handleSearchClick} />

            <div className='user-list'>
                {(searchedUsers.length > 0 ? searchedUsers : chatBase).map((chat, index) => (
                    <div key={index} className={`block-chat ${chat[1] ? '' : 'hidden'}`}>
                        <div className="info-chat">
                            <p className='name-user'>{chat[0]}</p>
                            <span className='activ'>{chat[1] ? "online" : "offline"}</span>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default MenuChats;
