import React, { useState } from "react";
import "../style/MainPage.css";
import axios from "axios";

const MyPage = () => {
  const chatBase = [
    ["Егор", true],
    ["Иван", false],
    ["Алексей", true],
    ["Максим", false],
    ["Никита", true],
    ["Сергей", true],
    ["Олег", false],
    ["Андрей", true],
    ["Дмитрий", false],
    ["Владимир", true],
  ];

  const [message, setMessage] = useState("");
  const [dataMessage, setDataMessage] = useState([]);
  const [textSearch, setTextSearch] = useState("");
  const [visible, setVisible] = useState(true);

  const handleMessage = (event) => {
    event.preventDefault();
    setMessage(event.target.value);
  };

  const sendMess = () => {
    const data = {
      messData: message,
    };

    setDataMessage([...dataMessage, data]);
    console.log(dataMessage);
    setMessage("");
  };

  const handleVisible = () => {
    setVisible(!visible);
  };

  const getSearchUser = async () => {
    try {
      const { data: dataUser } = await axios.get('http://localhost:7000/api/search-user', { params: { textSearch } });
      console.log(dataUser);
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <div className="container-my-chats">
      <div className="chatsUsers">
        <svg
          onClick={handleVisible}
          className="backSvg"
          width="60px"
          height="60px"
          viewBox="0 0 24 24"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          ...
        </svg>

        <div className="main-search-container">
          <input value={textSearch} onChange={(e) => setTextSearch(e.target.value)} className="inp-search" type="text" />
          <button onClick={getSearchUser} className="but-search">Найти</button>
        </div>

        {visible && (
          <div className="user-list">
            {chatBase.map((chat, index) => (
              <div key={index} className="block-chat">
                <div className="info-chat">
                  <p className="name-user">{chat[0]}</p>
                  <span className="activ">
                    {chat[1] ? "online" : "offline"}
                  </span>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="MyChat">
        <div className="block-name">
          <h1>Name</h1>
        </div>

        <div className="block-friend-mess">
          Приветsadfsdfsdf sdfsdfsdf sdfsdfsdf sdfsdfsdfsd sdfsdfsdfsd sdfsdfsdf
          sdfsdfsdt dsfsdf sde4wahdegdfashrerhyaerhaerhaert
          sde4wahdegdfashrerhyaerhaerhaerta
        </div>

        {dataMessage.map((mess, index) => (
          <div key={index} className="block-my-mess">
            {mess.messData}
          </div>
        ))}

        <div className="block-write">
          <input id="messageInput" onChange={handleMessage} type="text" />
          <button onClick={sendMess}>Отправить</button>
        </div>
      </div>
    </div>
  );
};

export default MyPage;
