import React, { useState } from 'react';
import axios from 'axios';

const Search = ({ onSearchClick, onBackClick }) => {
    const [user, setUser] = useState('');

    const handleUser = (event) => {
        event.preventDefault();
        setUser(event.target.value);
    };

    const handleSearch = async (event) => {
        try {
            const response = await axios.get('http://localhost:7000/search-user', { params: { username: user } });

            if (response.data.length > 0) {
                onSearchClick(response.data);
            } else {
                onSearchClick([]);
            }
        } catch (error) {
            console.error('Ошибка при поиске:', error);
        }
    };

    return (
        <div>
            <input onChange={handleUser} onClick={handleSearch} className='inp-search' type="text" />
            <button onClick={handleSearch} className="but-search">Найти</button>
        </div>
    );
};

export default Search;
