import React, { useState, useEffect } from "react";
import "../style/MainPage.css";
import axios from "axios";
import socketIOClient from "socket.io-client";
import { useLocation } from "react-router-dom";

const socket = socketIOClient("http://localhost:7000");

const MyPage = () => {
  const [message, setMessage] = useState("");
  const [dataMessage, setDataMessage] = useState([]);
  const [textSearch, setTextSearch] = useState("");
  const [visible, setVisible] = useState(true);
  const [newUser, setNewUser] = useState([]);
  const [friendsData, setFriendsData] = useState([]);
  const [friendId, setFriendId] = useState("");
  const [receivedMessages, setReceivedMessages] = useState([]);
  const [friendsLoaded, setFriendsLoaded] = useState(false);
  const location = useLocation();
  const search = location.search;
  const token = localStorage.getItem("token");

  const handleMessage = (event) => {
    setMessage(event.target.value);
  };

  useEffect(() => {
    const searchParams = Object.fromEntries(new URLSearchParams(search));
    console.log(searchParams);
  }, [search]);

  const sendMess = () => {
    const data = {
      messData: message,
    };

    setDataMessage([...dataMessage, data]);

    console.log(friendId);

    socket.emit("message", {
      userId: "userId",
      frendId: friendId,
      message: message,
    });

    setMessage(" "); // Очистка текста в поле ввода после отправки сообщения
  };

  const openChat = async (frendId) => {
    setFriendId(frendId.userId);

    const friendId = frendId.userId;
    console.log(friendId);

    socket.on("receiveMessage", (data) => {
      // Обработчик события приема сообщения
      setReceivedMessages([...receivedMessages, data]); // Добавление принятого сообщения в список
    });

    console.log(receivedMessages);

    try {
      const messageData = await axios.post(
        "http://localhost:7000/api/get-messange",
        {
          friendId,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      console.log(messageData.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleVisible = () => {
    setVisible(true);
  };

  const handleHide = () => {
    setVisible(false);
  };

  const saveUser = async (friendId) => {
    try {
      console.log("usr:", friendId);
      await axios.post(
        "http://localhost:7000/api/save-chats",
        {
          friendId,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
    } catch (error) {
      console.log(error);
    }
  };

  const getSearchUser = async () => {
    if (!textSearch || newUser.some((user) => user.userName === textSearch)) {
      return;
    }

    try {
      const dataUser = await axios.get(
        "http://localhost:7000/api/search-user",
        {
          params: { name: textSearch },
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const currentDate = new Date();
      const dataNewUser = {
        userName: dataUser.data.userName,
        userId: dataUser.data.userId,
        isOnline:
          currentDate.toLocaleDateString() +
          " " +
          currentDate.toLocaleTimeString(),
      };

      setNewUser([...newUser, dataNewUser]);
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    if (!friendsLoaded) {
      const fetchData = async () => {
        try {
          const response = await axios.get(
            "http://localhost:7000/api/get-frend",
            {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            }
          );

          if (response.data && response.data.myFriends) {
            const myFriends = response.data.myFriends;
            const updatedFriends = myFriends.map((friend) => ({
              userName: friend.userName,
              userId: friend.userId,
            }));

            setFriendsData(updatedFriends);
            setFriendsLoaded(true);
          } else {
            console.error("No friends found");
          }
        } catch (error) {
          console.error(error);
        }
      };

      fetchData();
    }
  }, []);

  return (
    <div className="container-my-chats">
      <div className="chatsUsers">
        <button onClick={handleVisible} className="backSvg">
          Back
        </button>

        <div className="main-search-container">
          <input
            onClick={handleHide}
            onChange={(e) => setTextSearch(e.target.value)}
            className="inp-search"
            type="text"
          />
          <button onClick={getSearchUser} className="but-search">
            Найти
          </button>
        </div>

        {visible ? (
          <div className="user-list">
            {friendsData.map((friend, index) => (
              <div
                key={index}
                onClick={() => openChat(friend)}
                className="block-chat"
              >
                <div className="info-chat">
                  <p className="name-user">{friend.userName}</p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="user-list">
            {newUser.map((chat, index) => (
              <div
                key={chat.userId}
                className="block-chat"
                onClick={() => saveUser(chat.userId)}
              >
                <div className="info-chat">
                  <p className="name-user">{chat.userName}</p>
                  <span className="activ">online</span>
                  <span className="activ">{chat.isOnline}</span>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="MyChat">
        <div className="block-name">
          <h1>{"myName"}</h1>
        </div>

        <div className="block-friend-mess">
          Приветsadfsdfsdf sdfsdfsdf sdfsdfsdf sdfsdfsdfsd sdfsdfsdfsd sdfsdfsdf
          sdfsdfsdt dsfsdf sde4wahdegdfashrerhyaerhaerhaerhaert
          sde4wahdegdfashrerhyaer haerhaerta
        </div>

        {dataMessage.map((mess, index) => (
          <div key={index} className="block-my-mess">
            {mess.messData}
          </div>
        ))}

        <div className="block-write">
          <input id="messageInput" onChange={handleMessage} type="text" />
          <button onClick={sendMess}>Отправить</button>
        </div>
      </div>
    </div>
  );
};

export default MyPage;
